        } else if ('deletelink' == key) {
          return config.baseUri + '/comment/delete/'
               + config.blogId + '/' + comment.id;
        } else if ('deleteclass' == key) {
          return comment.deleteclass;
        }
        return '';
      };

      var replybox = null;
      var replyUrlParts = null;
      var replyParent = undefined;

      var onReply = function(commentId, domId) {
        if (replybox == null) {
          // lazily cache replybox, and adjust to suit this style:
          replybox = document.getElementById('comment-editor');
          if (replybox != null) {
            replybox.height = '250px';
            replybox.style.display = 'block';
            replyUrlParts = replybox.src.split('#');
          }
        }
        if (replybox && (commentId !== replyParent)) {
          replybox.src = '';
          document.getElementById(domId).insertBefore(replybox, null);
          replybox.src = replyUrlParts[0]
              + (commentId ? '&parentID=' + commentId : '')
              + '#' + replyUrlParts[1];
          replyParent = commentId;
        }
      };

      var hash = (window.location.hash || '#').substring(1);
      var startThread, targetComment;
      if (/^comment-form_/.test(hash)) {
        startThread = hash.substring('comment-form_'.length);
      } else if (/^c[0-9]+$/.test(hash)) {
        targetComment = hash.substring(1);
      }

      // Configure commenting API:
      var configJso = {
        'maxDepth': config.maxThreadDepth
      };
      var provider = {
        'id': config.postId,
        'data': items,
        'loadNext': paginator,
        'hasMore': hasMore,
        'getMeta': getMeta,
        'onReply': onReply,
        'rendered': true,
        'initComment': targetComment,
        'initReplyThread': startThread,
        'config': configJso,
        'messages': msgs
      };

      var render = function() {
        if (window.goog && window.goog.comments) {
          var holder = document.getElementById('comment-holder');
          window.goog.comments.render(holder, provider);
        }
      };

      // render now, or queue to render when library loads:
      if (window.goog && window.goog.comments) {
        render();
      } else {
        window.goog = window.goog || {};
        window.goog.comments = window.goog.comments || {};
        window.goog.comments.loadQueue = window.goog.comments.loadQueue || [];
        window.goog.comments.loadQueue.push(render);
      }
    })();
//

body.script:
document.addEventListener("DOMContentLoaded", function () {
    // Menu Toggle
    const menuToggle = document.querySelector('.menu-toggle');
    const navMenu = document.querySelector('.nav-menu');
    menuToggle.addEventListener('click', () => {
        navMenu.classList.toggle('active');
        menuToggle.setAttribute('aria-expanded', navMenu.classList.contains('active'));
    });

    // Search Toggle
    const searchToggle = document.querySelector('.search-toggle');
    const searchForm = document.querySelector('.search-form');
    searchToggle.addEventListener('click', () => {
        searchForm.classList.toggle('active');
        searchToggle.setAttribute('aria-expanded', searchForm.classList.contains('active'));
    });

    // Load More
    const posts = document.querySelectorAll('.post');
    const maxVisible = 7;
    posts.forEach((el, i) => {
        if (i >= maxVisible) {
            el.setAttribute("data-hidden", "true");
        }
    });
    const loadMoreBtn = document.getElementById("load-more-btn");
    if (loadMoreBtn) {
        loadMoreBtn.addEventListener("click", () => {
            document.querySelectorAll('.post[data-hidden="true"]').forEach(el => {
                el.removeAttribute("data-hidden");
            });
            loadMoreBtn.textContent = 'No More Content';
            loadMoreBtn.disabled = true;
            loadMoreBtn.classList.add('load-more-disabled');
        });
    }

    // Trending Navigation (Placeholder)
    const trendingPrev = document.querySelector('.trending-prev');
    const trendingNext = document.querySelector('.trending-next');
    if (trendingPrev && trendingNext) {
        let index = 0;
        const articles = document.querySelectorAll('.article-card');
        trendingPrev.addEventListener('click', () => {
            if (index > 0) index--;
            updateTrending(index, articles);
        });
        trendingNext.addEventListener('click', () => {
            if (index < articles.length - 1) index++;
            updateTrending(index, articles);
        });
    }

    function updateTrending(index, articles) {
        articles.forEach((article, i) => {
            article.style.display = i === index ? 'flex' : 'none';
        });
    }

    // Lazy Loading
    const imgs = document.querySelectorAll('img');
    imgs.forEach(img => {
        img.loading = "lazy";
    });
});
