//

b:if.script:
document.addEventListener("DOMContentLoaded", function () {
  const posts = document.querySelectorAll('.post-outer');
  const maxVisible = 7;

  posts.forEach((el, i) => {
    if (i >= maxVisible) {
      el.setAttribute("data-hidden", "true");
    }
  });

  // Perluas fungsi tombol "Load More" dari template
  const loadMoreBtn = document.getElementById("magspot-load-more-link");
  if (loadMoreBtn) {
    loadMoreBtn.addEventListener("click", () => {
      document.querySelectorAll('.post-outer[data-hidden="true"]').forEach(el => {
        el.removeAttribute("data-hidden");
      });
    });
  }
});

html.script:
document.addEventListener("DOMContentLoaded", function () {
  const imgs = document.querySelectorAll('img');
  imgs.forEach(img => {
    img.loading = "lazy";
  });
});
